     * @param {string} userToken - The user token
     * @param {string} prepaidCardToken - The prepaid card token
     * @param {Object} options - The query parameters to send
     * @param {api-callback} callback - The callback for this call
     *
     * @throws Will throw an error if userToken or prepaidCardToken is not provided
     */
    listReceiptsForPrepaidCard(userToken, prepaidCardToken, options, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        if (!prepaidCardToken) {
            throw new Error("prepaidCardToken is required");
        }
        this.client.doGet(`users/${encodeURIComponent(userToken)}/prepaid-cards/${encodeURIComponent(prepaidCardToken)}/receipts`, options, Hyperwallet.handle204Response(callback));
    }

    //--------------------------------------
    // Webhooks: Notifications
    //-------------------------------------

    /**
     * List webhook notifications
     *
     * @param {Object} options - The query parameters to send
     * @param {api-callback} callback - The callback for this call
     */
    listWebhookNotifications(options, callback) {
        this.client.doGet("webhook-notifications", options, Hyperwallet.handle204Response(callback));
    }

    /**
     * Get a single webhook notification
     *
     * @param {string} webhookToken - Webhook token
     * @param {api-callback} callback - The callback for this call
     *
     * @throws Will throw an error if webhookToken is not provided
     */
    getWebhookNotification(webhookToken, callback) {
        if (!webhookToken) {
            throw new Error("webhookToken is required");
        }
        this.client.doGet(`webhook-notifications/${encodeURIComponent(webhookToken)}`, {}, callback);
    }

    //--------------------------------------
    // Internal utils
    //--------------------------------------

    /**
     * Add program token to data object if not already set
     *
     * @param {Object} data - The data object
     * @returns {Object} - The adjusted object
     *
     * @private
     */
    addProgramToken(data) {
        if (!data || !this.programToken) {
            return data;
        }
        if (data.programToken) {
            return data;
        }

        data.programToken = this.programToken; // eslint-disable-line no-param-reassign
        return data;
    }

    /**
     * Handle 204 response for list calls
     *
     * @param {api-callback} callback - The api callback
     * @returns {api-callback} - A wrapper api callback
     *
     * @private
     */
    static handle204Response(callback) {
        return (err, data, res) => {
            if (!err && res.status === 204) {
                callback(err, {
                    count: 0,
                    data: [],
                }, res);
                return;
            }
            callback(err, data, res);
        };
    }

    //--------------------------------------
    // Venmo Accounts
    //--------------------------------------

    /**
     * Create a Venmo account
     *
     * @param {string} userToken - The user token
     * @param {Object} data - The Venmo account data
     * @param {api-callback} callback - The callback for this call
     *
     * @throws Will throw an error if userToken is not provided
     */
    createVenmoAccount(userToken, data, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        if (!data.transferMethodCountry) {
            throw new Error("transferMethodCountry is required");
        }
        if (!data.transferMethodCurrency) {
            throw new Error("transferMethodCurrency is required");
        }
        if (!data.accountId) {
            throw new Error("Account is required");
        }
        this.client.doPost(`users/${encodeURIComponent(userToken)}/venmo-accounts`, data, {}, callback);
    }
    /**
     * Get a Venmo account
     *
     * @param {string} userToken - The user token
     * @param {string} venmoAccountToken - The venmo account token
     * @param {api-callback} callback - The callback for this call
     *
     * @throws Will throw an error if userToken or venmoAccountToken is not provided
     */
    getVenmoAccount(userToken, venmoAccountToken, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        if (!venmoAccountToken) {
            throw new Error("venmoAccountToken is required");
        }
        this.client.doGet(`users/${encodeURIComponent(userToken)}/venmo-accounts/${encodeURIComponent(venmoAccountToken)}`, {}, callback);
    }

    /**
     * List all Venmo accounts
     *
     * @param {string} userToken - The user token
     * @param {Object} options - The query parameters to send
     * @param {api-callback} callback - The callback for this call
     * @throws Will throw an error if userToken is not provided
     */
    listVenmoAccounts(userToken, options, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        this.client.doGet(`users/${encodeURIComponent(userToken)}/venmo-accounts`, options, Hyperwallet.handle204Response(callback));
    }

    /**
     * Update a venmo account
     *
     * @param {string} userToken - The user token
     * @param {string} venmoAccountToken - The bank account token
     * @param {Object} data - The venmo account data to update
     * @param {api-callback} callback - The callback for this call
     *
     * @throws Will throw an error if userToken or venmoAccountToken is not provided
     */
    updateVenmoAccount(userToken, venmoAccountToken, data, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        if (!venmoAccountToken) {
            throw new Error("venmoAccountToken is required");
        }
        this.client.doPut(`users/${encodeURIComponent(userToken)}/venmo-accounts/${encodeURIComponent(venmoAccountToken)}`, data, {}, callback);
    }

    /**
     * Deactivate a venmo account
     *
     * @param {string} userToken -  user token
     * @param {string} venmoAccountToken - Venmo account token
     * @param {api-callback} callback - callback for this call
     *
     * @throws Will throw an error if userToken or venmoAccountToken is not provided
     */

    deactivateVenmoAccount(userToken, venmoAccountToken, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        if (!venmoAccountToken) {
            throw new Error("venmoAccountToken is required");
        }

        const transition = {
            transition: "DE-ACTIVATED",
        };
        this.client.doPost(`users/${encodeURIComponent(userToken)}/venmo-accounts/${encodeURIComponent(venmoAccountToken)}/status-transitions`, transition, {}, callback);
    }

    /**
     * Create Venmo account status transition
     *
     * @param {string} userToken - The user token
     * @param {string} venmoAccountToken - venmo account token
     * @param {Object} data - Venmo account status transition data
     * @param {api-callback} callback - The callback for this call
     * @throws Will throw an error if userToken or venmoAccountToken is not provided
     */
    createVenmoAccountStatusTransition(userToken, venmoAccountToken, data, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        if (!venmoAccountToken) {
            throw new Error("venmoAccountToken is required");
        }

        this.client.doPost(`users/${encodeURIComponent(userToken)}/venmo-accounts/${encodeURIComponent(venmoAccountToken)}/status-transitions`, data, {}, callback);
    }
    /**
     * Get Venmo account status transition
     *
     * @param {string} userToken -user token
     * @param {string} venmoAccountToken - The venmo account token
     * @param {string} statusTransitionToken - The venmo account status transition token
     * @param {api-callback} callback - The callback for this call
     *
     * @throws Will throw an error if userToken or venmoAccountToken is not provided
     */
    getVenmoAccountStatusTransition(userToken, venmoAccountToken, statusTransitionToken, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        if (!venmoAccountToken) {
            throw new Error("venmoAccountToken is required");
        }
        if (!statusTransitionToken) {
            throw new Error("statusTransitionToken is required");
        }
        this.client.doGet(`users/${encodeURIComponent(userToken)}/venmo-accounts/${encodeURIComponent(venmoAccountToken)}/status-transitions/${encodeURIComponent(statusTransitionToken)}`,
            {},
            callback);
    }

    /**
     * List all venmo account status transitions
     *
     * @param {string} userToken - user token
     * @param {string} venmoAccountToken - venmo account token
     * @param {Object} options - query parameters to send
     * @param {api-callback} callback - callback for this call
     *
     * @throws Will throw an error if userToken or venmoAccountToken is not provided
     */
    listVenmoAccountStatusTransitions(userToken, venmoAccountToken, options, callback) {
        if (!userToken) {
            throw new Error("userToken is required");
        }
        if (!venmoAccountToken) {
            throw new Error("venmoAccountToken is required");
        }
        this.client.doGet(`users/${encodeURIComponent(userToken)}/venmo-accounts/${encodeURIComponent(venmoAccountToken)}/status-transitions`, options, Hyperwallet.handle204Response(callback));
    }
}

import request from "superagent";
import packageJson from "../../package.json";
import Encryption from "./Encryption";

/**
 * The callback interface for api calls
 *
 * @typedef {function} api-callback
 * @param {Object[]} [errors] - In case of an error an array with error objects otherwise undefined
 * @param {string} [errors[].fieldName] - The field name (if error is caused by a particular field)
 * @param {string} errors[].message - The error message
 * @param {string} errors[].code - The error code
 * @param {Object} data - The rest response body
 * @param {Object} res - The raw superagent response object
 */

/**
 * The Hyperwallet API Client
 */
export default class ApiClient {
    /**
     * Create a instance of the API client
     *
     * @param {string} username - The API username
     * @param {string} password - The API password
     * @param {string} server - The API server to connect to
     * @param {string} encryptionData - The API encryption data
     */
    constructor(username, password, server, encryptionData) {
        /**
         * The API username
         *
         * @type {string}
         * @protected
         */
        this.username = username;

        /**
         * The API password
         *
         * @type {string}
         * @protected
         */
        this.password = password;

        /**
         * The API server to connect to
         * @type {string}
         * @protected
         */
        this.server = server;

        /**
         * The Node SDK Version number
         *
         * @type {string}
         * @protected
         */
        this.version = packageJson.version;

        /**
         * The flag shows if encryption is enabled
         *
         * @type {boolean}
         * @protected
         */
        this.isEncrypted = false;

        if (encryptionData && encryptionData.clientPrivateKeySetPath && encryptionData.hyperwalletKeySetPath) {
            this.isEncrypted = true;
            this.clientPrivateKeySetPath = encryptionData.clientPrivateKeySetPath;
            this.hyperwalletKeySetPath = encryptionData.hyperwalletKeySetPath;
            this.encryption = new Encryption(this.clientPrivateKeySetPath, this.hyperwalletKeySetPath);
        }
    }

    /**
     * Do a POST call to the Hyperwallet API server
     *
     * @param {string} partialUrl - The api endpoint to call (gets prefixed by `server` and `/rest/v3/`)
     * @param {Object} data - The data to send to the server
     * @param {Object} params - Query parameters to send in this call
     * @param {api-callback} callback - The callback for this call
     */
    doPost(partialUrl, data, params, callback) {
        let contentType = "application/json";
        let accept = "application/json";
        let requestDataPromise = new Promise((resolve) => resolve(data));
        if (this.isEncrypted) {
            contentType = "application/jose+json";
            accept = "application/jose+json";
            this.createJoseJsonParser();
            requestDataPromise = this.encryption.encrypt(data);
        }
        requestDataPromise.then((requestData) => {
            request
                .post(`${this.server}/rest/v3/${partialUrl}`)
                .auth(this.username, this.password)
                .set("User-Agent", `Hyperwallet Node SDK v${this.version}`)
                .type(contentType)
                .accept(accept)
                .query(params)
                .send(requestData)
                .end(this.wrapCallback("POST", callback));
        }).catch(() => callback("Failed to encrypt body for POST request", undefined, undefined));
    }

    /**
     * Do a PUT call to the Hyperwallet API server to upload documents
     *
     * @param {string} partialUrl - The api endpoint to call (gets prefixed by `server` and `/rest/v3/`)
     * @param {Object} data - The data to send to the server
     * @param {api-callback} callback - The callback for this call
     */
    doPutMultipart(partialUrl, data, callback) {
        let contentType = "multipart/form-data";
        let accept = "application/json";
        /* eslint-disable no-unused-vars */
        const keys = Object.keys(data);
        /* eslint-enable no-unused-vars */

        let requestDataPromise = new Promise((resolve) => resolve(data));
        if (this.isEncrypted) {
            contentType = "multipart/form-data";
            accept = "application/jose+json";
            this.createJoseJsonParser();
            requestDataPromise = this.encryption.encrypt(data);
        }
        requestDataPromise.then(() => {
            const req = request
                .put(`${this.server}/rest/v3/${partialUrl}`)
                .auth(this.username, this.password)
                .set("User-Agent", `Hyperwallet Node SDK v${this.version}`)
                .type(contentType)
                .accept(accept);
            keys.forEach(key => {
                if (key === "data") {
                    req.field(key, JSON.stringify(data[key]));
                } else {
                    req.attach(key, data[key]);
                }
            });
            req.end(this.wrapCallback("PUT", callback));
        }).catch((err) => callback(err, undefined, undefined)).then((err, body, res) => callback(err, body, res));
    }

    /**
     * Do a PUT call to the Hyperwallet API server
     *
     * @param {string} partialUrl - The api endpoint to call (gets prefixed by server and /rest/v3/)
     * @param {Object} data - The data to send to the server
     * @param {Object} params - Query parameters to send in this call
     * @param {api-callback} callback - The callback for this call
     */
    doPut(partialUrl, data, params, callback) {
        let contentType = "application/json";
        let accept = "application/json";
        let requestDataPromise = new Promise((resolve) => resolve(data));
        if (this.isEncrypted) {
            contentType = "application/jose+json";
            accept = "application/jose+json";
            this.createJoseJsonParser();
            requestDataPromise = this.encryption.encrypt(data);
        }
        requestDataPromise.then((requestData) => {
            request
                .put(`${this.server}/rest/v3/${partialUrl}`)
                .auth(this.username, this.password)
                .set("User-Agent", `Hyperwallet Node SDK v${this.version}`)
                .type(contentType)
                .accept(accept)
                .query(params)
                .send(requestData)
                .end(this.wrapCallback("PUT", callback));
        }).catch(() => callback("Failed to encrypt body for PUT request", undefined, undefined));
    }

    /**
     * Do a GET call to the Hyperwallet API server
     *
     * @param {string} partialUrl - The api endpoint to call (gets prefixed by `server` and `/rest/v3/`)
     * @param {Object} params - Query parameters to send in this call
     * @param {api-callback} callback - The callback for this call
     */
    doGet(partialUrl, params, callback) {
        let contentType = "application/json";
        let accept = "application/json";
        if (this.isEncrypted) {
            contentType = "application/jose+json";
            accept = "application/jose+json";
            this.createJoseJsonParser();
        }
        request
            .get(`${this.server}/rest/v3/${partialUrl}`)
            .auth(this.username, this.password)
            .set("User-Agent", `Hyperwallet Node SDK v${this.version}`)
            .type(contentType)
            .accept(accept)
            .query(params)
            .end(this.wrapCallback("GET", callback));
    }

    /**
     * Wrap a callback to process possible API and network errors
     *
     * @param {string} httpMethod - The http method that is currently processing
     * @param {api-callback} callback - The final callback
     * @returns {function(err: Object, res: Object)} - The super agent callback
     *
     * @private
     */
    wrapCallback(httpMethod, callback = () => null) {
        return (err, res) => {
            const expectedContentType = (this.isEncrypted) ? "application/jose+json" : "application/json";
            const invalidContentType = res && res.header && res.status !== 204 && res.header["content-type"].indexOf(expectedContentType) === -1;
            if (invalidContentType) {
                callback([{
                    message: "Invalid Content-Type specified in Response Header",
                }], res ? res.body : undefined, res);
                return;
            }
            if (this.isEncrypted) {
                this.processEncryptedResponse(httpMethod, err, res.body, callback);
            } else {
                this.processNonEncryptedResponse(err, res, callback);
            }
        };
    }

    /**
     * Process non encrypted response from server
     *
     * @param {Object} err - Error object
     * @param {Object} res - Response object
     * @param {api-callback} callback - The final callback
     *
     * @private
     */
    processNonEncryptedResponse(err, res, callback) {
        if (!err) {
            callback(undefined, res.body, res);
            return;
        }

        let errors = [
            {
                message: `Could not communicate with ${this.server}`,
                code: "COMMUNICATION_ERROR",
            },
        ];
        if (res && res.body && res.body.errors) {
            errors = res.body.errors;
        }
        callback(errors, res ? res.body : undefined, res);
    }

    /**
     * Process encrypted response from server
     *
     * @param {string} httpMethod - The http method that is currently processing
     * @param {Object} err - Error object
     * @param {Object} res - Response object
     * @param {api-callback} callback - The final callback
     *
     * @private
     */
    processEncryptedResponse(httpMethod, err, res, callback) {
        if (!res) {
            callback("Try to decrypt empty response body", undefined, undefined);
        }
        this.encryption.decrypt(res)
            .then((decryptedData) => {
                const responseBody = JSON.parse(decryptedData.payload.toString());
                if (responseBody.errors) {
                    const responseWithErrors = {};
                    responseWithErrors.body = responseBody;
                    this.processNonEncryptedResponse(responseBody, responseWithErrors, callback);
                } else {
                    callback(undefined, responseBody, decryptedData);
                }
            })
            .catch(() => callback(`Failed to decrypt response for ${httpMethod} request`, res, res));
    }

    /**
     * Creates response body parser for application/jose+json content-type
     *
     * @private
     */
    createJoseJsonParser() {
        request.parse["application/jose+json"] = (res, callback) => {
            let data = "";
            res.on("data", (chunk) => {
                data += chunk;
            });
            res.on("end", () => {
                callback(null, data);
            });
        };
    }
}

import jose from "node-jose";
import fs from "fs";
import request from "superagent";

/**
 * The Hyperwallet Encryption processor
 */
export default class Encryption {
    /**
     * Create a instance of the Encryption service
     *
     * @param {string} clientPrivateKeySetLocation - String that can be a URL or path to file with client JWK set
     * @param {string} hyperwalletKeySetLocation - String that can be a URL or path to file with hyperwallet JWK set
     * @param {string} encryptionAlgorithm - JWE encryption algorithm, by default value = RSA-OAEP-256
     * @param {string} signAlgorithm - JWS signature algorithm, by default value = RS256
     * @param {string} encryptionMethod - JWE encryption method, by default value = A256CBC-HS512
     * @param {string} jwsExpirationMinutes - Minutes when JWS signature is valid
     */
    constructor(clientPrivateKeySetLocation, hyperwalletKeySetLocation, encryptionAlgorithm = "RSA-OAEP-256",
        signAlgorithm = "RS256", encryptionMethod = "A256CBC-HS512", jwsExpirationMinutes = 5) {
        /**
         * String that can be a URL or path to file with client JWK set
         *
         * @type {string}
         * @protected
         */
        this.clientPrivateKeySetLocation = clientPrivateKeySetLocation;

        /**
         * String that can be a URL or path to file with hyperwallet JWK set
         *
         * @type {string}
         * @protected
         */
        this.hyperwalletKeySetLocation = hyperwalletKeySetLocation;

        /**
         * Client KeyStore object
         *
         * @type {string}
         * @protected
         */
        this.clientKeyStore = null;

        /**
         * Hyperwallet KeyStore object
         *
         * @type {string}
         * @protected
         */
        this.hwKeyStore = null;

        /**
         * JWE encryption algorithm, by default value = RSA-OAEP-256
         *
         * @type {string}
         * @protected
         */
        this.encryptionAlgorithm = encryptionAlgorithm;

        /**
         * JWS signature algorithm, by default value = RS256
         *
         * @type {string}
         * @protected
         */
        this.signAlgorithm = signAlgorithm;

        /**
         * JWE encryption method, by default value = A256CBC-HS512
         *
         * @type {string}
         * @protected
         */
        this.encryptionMethod = encryptionMethod;

        /**
         * Minutes when JWS signature is valid, by default value = 5
         *
         * @type {number}
         * @protected
         */
        this.jwsExpirationMinutes = jwsExpirationMinutes;
    }

    /**
     * Makes an encrypted request : 1) signs the request body; 2) encrypts payload after signature
     *
     * @param {string} body - The request body to be encrypted
     */
    encrypt(body) {
        return new Promise((resolve, reject) => {
            const keyStorePromise = (this.clientKeyStore && this.hwKeyStore) ? Promise.resolve(this.keyStore) : this.createKeyStore();
            keyStorePromise
                .then(() => this.signBody(body))
                .then((signedBody) => this.encryptBody(signedBody))
                .then((result) => resolve(result))
                .catch((error) => reject(error));
        });
    }

    /**
     * Decrypts encrypted response : 1) decrypts the request body; 2) verifies the payload signature
     *
     * @param {string} body - The response body to be decrypted
     */
    decrypt(body) {
        return new Promise((resolve, reject) => {
            const keyStorePromise = this.keyStore ? Promise.resolve(this.keyStore) : this.createKeyStore();
            keyStorePromise
                .then(() => this.decryptBody(body))
                .then((decryptedBody) => this.checkSignature(decryptedBody.plaintext))
                .then((result) => resolve(result))
                .catch((error) => reject(error));
        });
    }

    /**
     * Verify if response body has a valid signature
     *
     * @param {string} body - The response body to be verified
     */
    checkSignature(body) {
        return new Promise((resolve, reject) => {
            const key = this.hwKeyStore.all({ alg: this.signAlgorithm })[0];
            if (!key) {
                reject(new Error(`JWK set doesn't contain key with algorithm = ${this.signAlgorithm}`));
            }
            const options = {
                handlers: {
                    exp: (jws) => {
                        if (this.getCurrentTime() > jws.header.exp) {
                            reject(new Error("JWS signature has expired"));
                        }
                    },
                },
            };
            jose.JWS.createVerify(key, options)
                .verify(body.toString())
                .then((result) => resolve(result))
                .catch(() => reject(new Error(`Failed to verify signature with key id = ${key.kid}`)));
        });
    }

    /**
     * Decrypts the response body
     *
     * @param {string} body - The response body to be decrypted
     */
    decryptBody(body) {
        return new Promise((resolve, reject) => {
            const key = this.clientKeyStore.all({ alg: this.encryptionAlgorithm })[0];
            if (!key) {
                reject(new Error(`JWK set doesn't contain key with algorithm = ${this.encryptionAlgorithm}`));
            }
            jose.JWE.createDecrypt(key)
                .decrypt(body)
                .then((result) => resolve(result))
                .catch(() => reject(new Error(`Failed to decrypt payload with key id = ${key.kid}`)));
        });
    }

    /**
     * Encrypts the request body
     *
     * @param {string} body - The request body to be encrypted
     */
    encryptBody(body) {
        return new Promise((resolve, reject) => {
            const key = this.hwKeyStore.all({ alg: this.encryptionAlgorithm })[0];
            if (!key) {
                reject(new Error(`JWK set doesn't contain key with algorithm = ${this.encryptionAlgorithm}`));
            }
            const encryptionHeader = {
                format: "compact",
                alg: key.alg,
                enc: this.encryptionMethod,
                kid: key.kid,
            };
            jose.JWE.createEncrypt(encryptionHeader, key)
                .update(body)
                .final()
                .then((result) => resolve(result))
                .catch(() => reject(new Error(`Failed to encrypt payload with key id = ${key.kid}`)));
        });
    }

    /**
     * Makes signature for request body
     *
     * @param {string} body - The request body to be signed
     */
    signBody(body) {
        return new Promise((resolve, reject) => {
            const key = this.clientKeyStore.all({ alg: this.signAlgorithm })[0];
            if (!key) {
                reject(new Error(`JWK set doesn't contain key with algorithm = ${this.signAlgorithm}`));
            }
            const signHeader = {
                format: "compact",
                alg: key.alg,
                fields: {
                    crit: ["exp"],
                    exp: this.getSignatureExpirationTime(),
                    kid: key.kid,
                },
            };
            jose.JWS.createSign(signHeader, key)
                .update(JSON.stringify(body), "utf8")
                .final()
                .then((result) => resolve(result))
                .catch(() => reject(new Error(`Failed to sign with key id = ${key.kid}`)));
        });
    }

    /**
     * Calculates signature expiration time in seconds ( by default expiration time = 5 minutes )
     */
    getSignatureExpirationTime() {
        const millisecondsInMinute = 60000;
        const millisecondsInSecond = 1000;
        const currentTime = new Date(new Date().getTime() + this.jwsExpirationMinutes * millisecondsInMinute).getTime();
        return Math.round(currentTime / millisecondsInSecond);
    }

    /**
     * Get current time in seconds
     */
    getCurrentTime() {
        const millisecondsInSecond = 1000;
        return Math.round(new Date().getTime() / millisecondsInSecond);
    }

    /**
     * Creates 2 JWK key stores : 1) for client keys 2) for hyperwallet keys
     */
    createKeyStore() {
        return new Promise((resolve, reject) => {
            this.readKeySet(this.hyperwalletKeySetLocation)
                .then((jwkSet) => this.createKeyStoreFromJwkSet(this.hyperwalletKeySetLocation, jwkSet))
                .then(() => this.readKeySet(this.clientPrivateKeySetLocation))
                .then((jwkSet) => this.createKeyStoreFromJwkSet(this.clientPrivateKeySetLocation, jwkSet))
                .then((result) => resolve(result))
                .catch((error) => reject(error));
        });
    }

    /**
     * Converts JWK set in JSON format to JOSE key store format
     *
     * @param {string} jwkSetPath - The location of JWK set (can be URL string or path to file)
     * @param {string} jwkSet - The JSON representation of JWK set, to be converted to keystore
     */
    createKeyStoreFromJwkSet(jwkSetPath, jwkSet) {
        return new Promise((resolve, reject) => {
            jose.JWK.asKeyStore(jwkSet).
                then((result) => {
                    if (jwkSetPath === this.clientPrivateKeySetLocation) {
                        this.clientKeyStore = result;
                    } else {
                        this.hwKeyStore = result;
                    }
                    resolve(result);
                })
                .catch(() => reject(new Error("Failed to create keyStore from given jwkSet")));
        });
    }

    /**
     * Reads JWK set in JSON format either from given URL or path to local file
     *
     * @param {string} keySetPath - The location of JWK set (can be URL string or path to file)
     */
    readKeySet(keySetPath) {
        return new Promise((resolve, reject) => {
            if (fs.existsSync(keySetPath)) {
                fs.readFile(keySetPath, { encoding: "utf-8" }, (err, keySetData) => {
                    if (!err) {
                        resolve(keySetData);
                    } else {
                        reject(new Error(err));
                    }
                });
            } else {
                this.checkUrlIsValid(keySetPath, (isValid) => {
                    if (isValid) {
                        request(keySetPath, (error, response) => {
                            if (!error) {
                                const responseBody = response.body && Object.keys(response.body).length !== 0 ? response.body : response.text;
                                resolve(responseBody);
                            }
                        });
                    } else {
                        reject(new Error(`Wrong JWK set location path = ${keySetPath}`));
                    }
                });
            }
        });
    }

    /**
     * Checks if an input string is a valid URL
     *
     * @param {string} url - The URL string to be verified
     * @param {string} callback - The callback method to process the verification result of input url
     */
    checkUrlIsValid(url, callback) {
        request(url, (error, response) => {
            callback(!error && response.statusCode === 200);
        });
    }

    /**
     * Convert encrypted string to array of Buffer
     *
     * @param {string} encryptedBody - Encrypted body to be decoded
     */
    base64Decode(encryptedBody) {
        const parts = encryptedBody.split(".");
        const decodedParts = [];
        parts.forEach(elem => {
            decodedParts.push(jose.util.base64url.decode(elem));
        });
        const decodedBody = {};
        decodedBody.parts = decodedParts;
        return decodedBody;
    }

    /**
     * Convert array of Buffer to encrypted string
     *
     * @param {string} decodedBody - Array of Buffer to be decoded to encrypted string
     */
    base64Encode(decodedBody) {
        const encodedParts = [];
        decodedBody.parts.forEach(part => {
            encodedParts.push(jose.util.base64url.encode(Buffer.from(JSON.parse(JSON.stringify(part)).data)));
        });
        return encodedParts.join(".");
    }
}

